function binaryTreeDepth(root) {
  // Handle null, undefined, or empty cases
  if (root === null || root === undefined) return 0;
  // Handle case where root might be an empty array (from failed conversion)
  if (Array.isArray(root) && root.length === 0) return 0;
  // Additional safety check for unexpected types
  if (typeof root !== 'object') return 0;
  // For safety, check if left and right properties exist before accessing
  const leftDepth = root.left !== undefined && root.left !== null ? binaryTreeDepth(root.left) : 0;
  const rightDepth = root.right !== undefined && root.right !== null ? binaryTreeDepth(root.right) : 0;
  return Math.max(leftDepth, rightDepth) + 1;
}
